INSERT INTO `omeka_simple_pages_pages` VALUES (1,7,7,1,'About Us','about-us','Special Collections is located in Carol M. Newman Library at Virginia Tech. Special Collections includes manuscript collections, rare books, and the University Archives. The collection also contains state, local and historical maps and photographs. Our mission is to collect and preserve unique, historical materials and provide access to them in their original form.\r\n\r\n<p>\r\nSend questions or comments to:<br>\r\nspecref@vt.edu<br>\r\n<a href=\"http://www.lib.vt.edu/University Libraries\">University Libraries</a><br>\r\n<a href=\"http://www.vt.edu/\">Virginia Tech</a>, P.O. Box 90001,<br>\r\nBlacksburg, VA 24062-9001<br>\r\n</p>\r\n<a href=\"room-115\">Click here for Information on our Digitization Services</a>','2014-08-21 23:29:26','2013-10-15 19:54:47',0,0,'',0),(2,7,7,1,'Civil War Collection','civil-war-collection','<h3>Items in the Civil War Collection</h3>\r\n<?php $CivilWarItems=get_records(\'Item\', array(\'advanced\' => array(array(\'element_id\' => 49, \'type\' => \'is exactly\', \'terms\' => \'Civil War\'))), 200); ?>\r\n<?php set_loop_records(\'Items\', $CivilWarItems); ?>\r\n<?php if(has_loop_records(\'Items\')): ?>\r\n<?php foreach(loop(\'Items\') as $item): ?>\r\n\r\n<div class=\"collection-panel\">\r\n<div id=\"collection-container\">\r\n<?php if(metadata(\'item\', \'has thumbnail\')): ?>\r\n<div class=\"panel-image\">\r\n <?php echo link_to_item(item_image(\'thumbnail\')); ?> \r\n</div>\r\n<?php endif; ?>\r\n\r\n<div class=\"item-meta\">\r\n <h2><?php echo link_to_item(metadata(\'item\', array(\'Dublin Core\', \'Title\')), array(\'class\'=>\'permalink\')); ?></h2>\r\n\r\n\r\n<?php if($itemAbstract = metadata(\'item\', array(\'Dublin Core\', \'Abstract\'))): ?>\r\n                <div class=\"item-description\"><?php echo $itemAbstract; ?>\r\n</div>\r\n<?php elseif($itemDescription = metadata(\'item\', array(\'Dublin Core\', \'Description\'), array(\'snippet\'=>150))): ?